import React, { useState } from 'react';
import Navbar from '../../../layouts/frontend/Navbar';
import axios from 'axios';
import swal from 'sweetalert';
import { useHistory } from 'react-router';
import { Link } from 'react-router-dom';

function Register() {

    const history = useHistory();

    const [registerInput, setRegister] = useState({
        name: '',
        email: '',
        password: '',
        error_list: [],
    });

    const handleInput = (event) => {
        event.persist();
        setRegister({ ...registerInput, [event.target.name]: event.target.value });
    }

    const registerSubmit = (event) => {
        event.preventDefault();

        const data = {
            name: registerInput.name,
            email: registerInput.email,
            password: registerInput.password,
        }

        axios.get('/sanctum/csrf-cookie').then(response => {
            axios.post(`/api/register`, data).then(res => {
                if (res.data.status === 200) {
                    localStorage.setItem('auth_token', res.data.token);
                    localStorage.setItem('auth_name', res.data.username);
                    swal("Success", res.data.message, "success");
                    history.push('/');
                }
                else {
                    setRegister({ ...registerInput, error_list: res.data.validation_errors });
                }
            });
        });
    }

    return (
        <div>
            <Navbar />

            <header className="ex-header">
                <div className="container">
                    <div className="row">
                        <div className="col-xl-10 offset-xl-1">
                            <h1 className="text-center">Sign Up</h1>
                        </div>
                    </div>
                </div>
            </header>

            {/* start sign up */}
            <div className="ex-form-1 pt-3 pb-3">
                <div className="container">
                    <div className="row">
                        <div className="col-xl-6 offset-xl-3">
                            <div className="text-box mt-3 mb-3">
                                <p className="mb-4">Fill out the form below to sign up for the service. Already signed up? Then just&nbsp;
                                    <Link className="blue" to="/login">Log In</Link></p>
                                <form >
                                    <div className="mb-4 form-group">
                                        <label>Full Name</label>
                                        <input type="" name="name" onChange={handleInput} value={registerInput.name} className="form-control" />
                                        
                                    </div>
                                    <div className="mb-4 form-group">
                                        <label>Email ID</label>
                                        <input type=""  value={registerInput.email} className="form-control" />
                                        
                                    </div>
                                    <div className="mb-4 form-group">
                                        <label>Password</label>
                                        <input type=""  value={registerInput.password} className="form-control" />
                                        
                                    </div>
                                    <div className="mb-4 form-group">
                                        <button type="submit" className="form-control-submit-button">Sign Up</button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <div className="ex-form-1 pt-5 pb-5">
  <div className="container">
    <div className="row">
      <div className="col-xl-6 offset-xl-3">
        <div className="text-box mt-5 mb-5">
          <p className="mb-4">Fill out the form below to sign up for the service. Already signed up? Then just
            <Link className="blue" to="/login">Log In</Link></p>
          <form onSubmit={registerSubmit}>
          <div className="mb-4 form-floating">
              <input type="text" name="name" onChange={handleInput} className="form-control" id="floatingInput2" placeholder="Your name" />
              <label htmlFor="floatingInput">Your name</label>
              <span>{registerInput.error_list.name}</span>
            </div>
            <div className="mb-4 form-floating">
              <input type="email" name="email" onChange={handleInput} className="form-control" id="floatingInput" placeholder="name@example.com" />
              <label htmlFor="floatingInput">Email address</label>
              <span>{registerInput.error_list.email}</span>
            </div>
            <div className="mb-4 form-floating">
              <input type="password" className="form-control" id="floatingPassword" placeholder="Password" />
              <label htmlFor="floatingPassword">Password</label>
              <span>{registerInput.error_list.password}</span>
            </div>
            <div className="mb-4 form-check">
              <input type="checkbox" className="form-check-input" id="exampleCheck1" />
              <label className="form-check-label" htmlFor="exampleCheck1">I agree with the site's stated <a href="privacy.html">Privacy Policy</a> and <a href="terms.html">Terms &amp;
                  Conditions</a></label>
            </div>
            <button type="submit" className="form-control-submit-button">Sign up</button>
          </form>
        </div>
      </div>
    </div>
  </div>
</div>

            {/* end */}
        </div>
    );
}

export default Register;
