import React, { useState } from "react";
import Navbar from "../../../layouts/frontend/Navbar";
import axios from "axios";
import swal from "sweetalert";
import { useHistory } from "react-router";
import { Link } from "react-router-dom";

function AddProfile() {
  const history = useHistory();

  const [AddProfileInput, setAddProfile] = useState({
    fullname: "",
    username: "",
    email: "",
    password: "",
    phone: "",
    error_list: [],
  });

  const handleInput = (event) => {
    event.persist();
    setAddProfile({ ...AddProfileInput, [event.target.name]: event.target.value });
  };

  const AddProfileSubmit = (event) => {
    event.preventDefault();

    const data = {
      fullname: AddProfileInput.fullname,
        username: AddProfileInput.username,
      email: AddProfileInput.email,
      password: AddProfileInput.password,
        phone: AddProfileInput.phone,
    };

    axios.get("/sanctum/csrf-cookie").then((response) => {
      axios.post(`api/register`, data).then((res) => {
        if (res.data.status === 200) {
          localStorage.setItem("auth_token", res.data.token);
          localStorage.setItem("auth_name", res.data.username);
          history.push("/admin/profile");
        } else {
          setAddProfile({
            ...AddProfileInput,
            error_list: res.data.validation_errors,
          });
        }
      });
    });
  };

  return (
    <div className="ex-form-1 pt-5 pb-5">
        <div className="container">
          <div className="row">
            <div className="col-xl-6 offset-xl-3">
              <div className="text-box mt-5 mb-5">
                <form onSubmit={AddProfileSubmit}>
                  <div className="form-group mb-3">
                    <label>Full Name</label>
                    <input
                      type="text"
                      name="fullname"
                      placeholder="Nhập họ và tên"
                      onChange={handleInput}
                      value={AddProfileInput.fullname}
                      className="form-control"
                    />
                    <span>{AddProfileInput.error_list.fullname}</span>
                  </div>
                  <div className="form-group mb-3">
                    <label>Username</label>
                    <input
                      type="text"
                      name="username"
                    placeholder="Nhập tên đăng nhập"
                      onChange={handleInput}
                      value={AddProfileInput.username}
                      className="form-control"
                    />
                    <span>{AddProfileInput.error_list.username}</span>
                  </div>
                  <div className="form-group mb-3">
                    <label>Email</label>
                    <input
                      type="email"
                      name="email"
                      onChange={handleInput}
                      value={AddProfileInput.email}
                      className="form-control"
                    />
                    <span>{AddProfileInput.error_list.email}</span>
                  </div>
                  <div className="form-group mb-3">
                    <label>Password</label>
                    <input
                      type="password"
                      name="password"
                      onChange={handleInput}
                      value={AddProfileInput.password}
                      className="form-control"
                    />
                    <span>{AddProfileInput.error_list.password}</span>
                  </div>
                    <div className="form-group mb-3">
                    <label>Phone</label>
                    <input
                        type="text"
                        name="phone"
                        onChange={handleInput}
                        value={AddProfileInput.phone}
                        className="form-control"
                    />
                    <span>{AddProfileInput.error_list.phone}</span>
                  </div>
                  <div className="form-group mb-3">
                    <button
                      type="submit"
                      className="form-control-submit-btn"
                    >
                      Thêm mới
                    </button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
  );
}

export default AddProfile;
