import Navbar from "../../../layouts/frontend/Navbar";
import ScrollButton from "../../../layouts/frontend/ScrollButton";
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

function ListQuiz() {

    const [category, setCategory] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {

        axios.get(`/api/get-category-quiz`).then(res => {
            if (res.data.status === 200) {
                setCategory(res.data.category);
                setLoading(false);
            }
        });

    }, [])

    if (loading) {
        return (
            <h4>Loading Category...</h4>
        )
    } else {
        var showCategoryList = '';
        showCategoryList = category.map((item) => {
            return (
                <div className="col-md-4" key={item.id}>
                    <div className="card">
                        <Link></Link>
                        <div className="card-body">
                            <h5 className="card-title text-center">{item.name}</h5>
                            <p className="card-text">{item.description}</p>
                            <Link className="btn-outline-sm" to="/quiztext">Vào thi</Link>
                        </div>
                    </div>
                </div>
            )
        });
    }

    return (
        <div>
            <Navbar />

            <header className="ex-header">
                <div className="container">
                    <div className="row">
                        <div className="col-xl-10 offset-xl-1">
                            <h1 className="text-center">Danh Sách Bài Test</h1>
                        </div>
                    </div>
                </div>
            </header>

            <div className="container">
                <div className="row row-cols-1 row-cols-md-4 g-4 mt-5">
                    {showCategoryList}
                </div>
            </div>
            <ScrollButton />
        </div>
    )
}

export default ListQuiz;
