import axios from "axios";
import React, { useEffect } from "react";
import { Link } from 'react-router-dom';

function AddItem() {

const [categorylist, setcategorylist] = useState([])

    useEffect(() => {

        axios.get(`/api/all-category-quiz`).then(res => {
            if (res.data.status === 200) {
                console.log(res.data.category);
            }
        });

    }, [])

    return (
        <div className="container-fluid px-4">
            <div className="card mt-4">
                <div className="card-header">
                    <h4>Add Item
                        <Link to="/admin/view-item-quiz" className="btn btn-primary btn-sm float-end">View Item</Link>
                    </h4>
                </div>
                <div className="card-body">
                    <form encType="multipart/form-data">
                        <div className="form-group mb-2">
                            <label className="form-label">Select category</label>
                            <select name="category_id" className="form-control">
                                <option>Select option</option>
                            </select>
                        </div>
                        <div className="form-group mb-2">
                            <label className="form-label">Tên danh mục</label>
                            <input type="text" name="name" className="form-control" />
                        </div>
                        <div className="form-group mb-2">
                            <label className="form-label">Mô tả</label>
                            <input type="text" name="description" className="form-control" />
                        </div>
                        <div className="form-group mb-2">
                            <label className="form-label">Hình ảnh</label>
                            <input type="file" name="image" className="form-control" />
                        </div>
                        <div className="form-group mb-2">
                            <label className="form-label">Trạng thái&nbsp;&nbsp;</label>
                            <input type="checkbox" name="status" className="form-check-input" />
                        </div>
                        <button type="submit" className="btn btn-primary form-control">Thêm</button>
                    </form>
                </div>
            </div>
        </div>
    )
}

export default AddItem;
