import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

function ListQuizItem(props) {

    const [category, setCategory] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {

        axios.get(`/api/get-category-quiz`).then(res => {
            if (res.data.status === 200) {
                setCategory(res.data.category);
                setLoading(false);
            }
        });

    }, [])

    if (loading) {
        return (
            <h4>Loading Category...</h4>
        )
    } else {
        var showCategoryList = '';
        showCategoryList = category.map((item) => {
            return (
                <div className="col-md-4">
                    <div className="card">
                        <h5 className="card-title text-center">{item.name}</h5>
                        <p className="card-text">{item.desc}</p>
                        <Link className="btn-outline-sm" to="/quiztext">VÃ o thi</Link>
                    </div>
                </div>
            )
        });
    }

    return (
        { showCategoryList }
    )
}

export default ListQuizItem;
