import axios from "axios";
import React from "react";
import { Link } from "react-router-dom";

class Profile extends React.Component {
  state = {
    listUser: [],
  };

  async componentDidMount() {
    let res = await axios.get(`/api/profile`);
    this.setState({
      listUser: res.data,
    });
    console.log(res);
  }
  // Function handle delete user
  handleDeleteUser = (id) => {
    if (window.confirm("Bạn có chắc chắn muốn xoá người dùng này không?")) {
      axios.delete(`api/delete/${id}`).then((res) => {
        window.alert("Xoá thành công");
        window.location.reload();
      });
    } else {
      window.alert("Xoá thất bại");
    }
  };
  // Create function update user by hook and axios
  handleUpdateUser = (id) => {
    
  };

  render() {
    let { listUser } = this.state;
    return (
      <div className="container">
        <div className="row">
          <div>
            <div className="col-xs-12 col-sm- col-md- col-lg-12">
              <br />
              <Link
                to="/admin/profile/add"
                class="btn btn-default btn-info mb-10"
              >
                Thêm người dùng
              </Link>

              <div className="panel panel-primary">
                <div className="panel-heading">
                  <h3 className="panel-title">Danh sách người dùng</h3>
                </div>
                <div className="panel-body">
                  <table className="table table-bordered table-hover">
                    <thead>
                      <tr>
                        <th>ID</th>
                        <th>Họ và tên</th>
                        <th>Username</th>
                        <th>Email</th>
                        <th>Phone</th>
                        <th>Thao tác</th>
                      </tr>
                    </thead>
                    <tbody>
                      {listUser &&
                        listUser.length > 0 &&
                        listUser.map((item, index) => {
                          return (
                            <>
                              <tr>
                                <td>{index + 1}</td>
                                <td>{item.fullname}</td>
                                <td>{item.username}</td>
                                <td>{item.email}</td>
                                <td>{item.phone}</td>
                                <td>
                                  <button
                                    type="button"
                                    class="btn btn-danger mr-10"
                                    onClick={() =>
                                      this.handleDeleteUser(item.id)
                                    }
                                  >
                                    Xoá
                                  </button>

                                  <button
                                    type="button"
                                    className="btn btn-success"
                                    onClick={() =>
                                      this.handleUpdateUser()
                                    }
                                  >
                                    Update
                                  </button>
                                </td>
                              </tr>
                            </>
                          );
                        })}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Profile;
