import React, { Component } from "react";
import { Table, Button } from "reactstrap";
import axios from "axios";
import AddProfile from "./AddProfile";

class Profile extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            Users: [],
            newUserData: {
              first_name: "",
              last_name: "",
              email: "",
              phone: "",
            },
            isLoading: false,
            status: "",
            newUserModal: false,
        }
    }

    componentDidMount() {
        this.getUsers();
      }      
    getUsers() {
    axios.get("http://localhost:8000/api/Users").then((response) => {
        if (response.status === 200) {
        this.setState({
            Users: response.data.data ? response.data.data : [],
        });
        }
        if (
        response.data.status === "failed" &&
        response.data.success === false
        ) {
        this.setState({
            noDataFound: response.data.message,
        });
        }
    });
    } 

    toggleNewUserModal = () => {
        this.setState({
          newUserModal: !this.state.newUserModal,
        });
      };
    onChangeAddUserHandler = (e) => {
        let { newUserData } = this.state;
        newUserData[e.target.name] = e.target.value;
        this.setState({ newUserData });
    };
    addUser = () => {
        axios
          .post(
            "http://localhost:8000/api/create-User",
            this.state.newUserData
          )
          .then((response) => {
            const { Users } = this.state;
            const newUsers = [...Users];
            newUsers.push(response.data);
            this.setState(
              {
                Users: newUsers,
                newUserModal: false,
                newUserData: {
                  first_name: "",
                  last_name: "",
                  email: "",
                  phone: "",
                },
              },
              () => this.getUsers()
            );
          });
      };
    
  render() {
    const { newUserData, noDataFound, Users} = this.state;
      let UsersDetails = [];
      if (Users.length) {
        UsersDetails = Users.map((item,index) => {
          return (
            <tr key={item.id}>
              <td>{index + 1}</td>
              <td>{item.first_name}</td>
              <td>{item.last_name}</td>
              <td>{item.full_name}</td>
              <td>{item.email}</td>
              <td>{item.phone}</td>
              <td>
                <Button
                  color="success"
                  className="mr-3"
                  size="sm"
                >
                  Edit
                </Button>
                <Button
                  color="danger"
                  size="sm"
                >
                  Delete
                </Button>
              </td>
            </tr>
          );
        });
      }
  
      if (this.state.isLoading) {
        return <div className="spinner-border text-center" role="status"> <span className="sr-only">Loading...</span>
      </div>
      } 
    return (
      <div className="App container mt-4">
           <h4 className="font-weight-bold">Users Registration</h4> 
           <AddProfile
                toggleNewUserModal={this.toggleNewUserModal}
                newUserModal={this.state.newUserModal}
                onChangeAddUserHandler={this.onChangeAddUserHandler}
                addUser={this.addUser}
                newUserData={newUserData}
          />
        <Table>
          <thead>
            <tr>
              <th>#</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Full Name</th>
              <th>Email</th>
              <th>Phone</th>
              <th>Actions</th>
            </tr>
          </thead>
          {Users.length === 0 ? (
            <tbody>
              <h3>{noDataFound}</h3>
            </tbody>
          ) : (
            <tbody>{UsersDetails}</tbody>
          )}
        </Table>
      </div>
    );
  }
}
export default Profile;
