import axios from "axios";
import React from "react";

class Profile extends React.Component {
  state = {
    listUser: [],
  };
  async componentDidMount() {
    let res = await axios.get(`/api/profile`);
    this.setState({
      listUser: res.data,
    });
    console.log(res);
  }

  render() {
   
    return (
      <div className="container">
    <div className="row">
    
    <div>
      <div className="col-xs-12 col-sm- col-md- col-lg-12">
        <br />
        <button type="button" class="btn btn-default btn-info mb-10">
          Thêm người dùng
        </button>

        <div className="panel panel-primary">
          <div className="panel-heading">
            <h3 className="panel-title">Danh sách người dùng</h3>
          </div>
          <div className="panel-body">
          
            <table className="table table-bordered table-hover">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Họ và tên</th>
                  <th>Username</th>
                  <th>Email</th>
                  <th>Phone</th>
                  <th>Thao tác</th>
                </tr>
              </thead>
              <tbody>
              
      <tr>
      {listUser && listUser.length > 0 && listUser.map((item, index) => {
              return (
                <>
                <td>{index+1}</td>
                <td>{item.fullname}</td>
                <td>{item.username}</td>
                <td>{item.email}</td>
                <td>{item.phone}</td>
                <td>
          <button type="button" class="btn btn-danger mr-10">
            Xoá
          </button>

          <button type="button" className="btn btn-success">
            Update
          </button>
        </td>
        </> );
        
      })}

        
      </tr>
                
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
    </div>
  </div>
      
    );
  }
}

export default Profile;
