import axios from "axios";
import React,{ useState, useEffect} from "react";
import {history} from 'react-router'
import swal from "sweetalert";
import ScrollButton from "../../../layouts/frontend/ScrollButton";

function ViewQuiz() {

    const [loading, setLoading] = useState(true);
    const [quiz, setQuiz] = useState([]);
    const [item, setItem] = useState([]);

    useEffect(() => {

        let isMounted = true;

        axios.get().then(res => {
            if (isMounted) {
                if (res.data.status === 200) {
                    setQuiz(res.data.quiz_data.quiz);
                    setItem(res.data.quiz_data.item);
                } else if (res.data.status === 404) {
                    history.push('/listquiz');
                    swal("Error", res.data.status,"error")
                }
            }
        });

        return () => {
            isMounted = false;
        }
    }, [history])

    if (loading) {
        return <h4>Loading Quiz...</h4>
    } else {

    }

    return (
        <div>
            <header className="ex-header">
                <div className="container">
                    <div className="row">
                        <div className="col-xl-10 offset-xl-1">
                            <h1 className="text-center">Danh Sách Câu Hỏi</h1>
                        </div>
                    </div>
                </div>
            </header>
            <div className="py-3 bg-warning">
                <div className="container">
                    <h6>item name / quiz name</h6>
                </div>
            </div>
            <div className="container">
                <div className="row row-cols-1 row-cols-md-4 g-4 mt-5">
                    {/* {showQuizList} */}
                </div>
            </div>
            <ScrollButton />
        </div>
    )
}

export default ViewQuiz;
