import axios from "axios";
import React, { useState, useEffect } from "react";
import { Link } from 'react-router-dom';

function AddQuiz() {

    const [itemlist, setItemlist] = useState([]);

    useEffect(() => {

        axios.get(`/api/all-item-quiz`).then(res => {
            if (res.data.status === 200) {
                setItemlist(res.data.item);
            }
        });

    }, [])

    return (
        <div className="container-fluid px-4">
            <div className="card mt-4">
                <div className="card-header">
                    <h4>Add Quiz
                        <Link to="/admin/view-quiz" className="btn btn-primary btn-sm float-end">View Quiz</Link>
                    </h4>
                </div>
                <div className="card-body">
                    <form>
                        <div className="form-group mb-2">
                            <label className="form-label">Select category</label>
                            <select name="category_id" className="form-select form-control">
                                <option>Select option</option>

                                {
                                    itemlist.map((item) => {
                                        return (
                                            <option value={item.id} key={item.id}>{item.name}</option>
                                        )
                                    })
                                }

                            </select>
                            <small className="text-danger"></small>
                        </div>
                        <div className="form-group mb-2">
                            <label className="form-label">Tên danh mục</label>
                            <input type="text" name="name" className="form-control" />
                            <small className="text-danger"></small>
                        </div>
                        <div className="form-group mb-2">
                            <label className="form-label">Mô tả</label>
                            <input type="text" name="description" className="form-control" />
                            <small className="text-danger"></small>
                        </div>
                        <div className="form-group mb-2">
                            <label className="form-label">Hình ảnh</label>
                            <input type="file" name="image" className="form-control" />
                            <small className="text-danger"></small>
                        </div>
                        <div className="form-group mb-2">
                            <label className="form-label">Trạng thái&nbsp;&nbsp;</label>
                            <input type="checkbox" name="status" className="form-check-input" />
                        </div>
                        <button type="submit" className="btn btn-primary px-4 mt-2">Thêm</button>
                    </form>
                </div>
            </div>
        </div>
    )
}

export default AddQuiz;
