import React, { Component } from "react";
import { Table, Button } from "reactstrap";
import AddProfile from "./AddProfile";
import axios from "axios";
export default class Profile extends Component {
  constructor(props) {
    super(props);
    this.state = {
      students: [],
    };
  }

  componentDidMount() {
    this.getStudents();
  }
  getStudents() {
    axios.get("http://localhost:8000/api//api/register").then((response) => {
      if (response.status === 200) {
        this.setState({
          students: response.data.data ? response.data.data : [],
        });
      }
      if (
        response.data.status === "failed" &&
        response.data.success === false
      ) {
        this.setState({
          noDataFound: response.data.message,
        });
      }
    });
  }

  render() {
    return (
      <div className="App container mt-4">
        <h4 className="font-weight-bold">Quản lý thành viên</h4>
        <AddProfile />
        <Table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Họ và tên</th>
              <th>Username</th>
              <th>Email</th>
              <th>Phone</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>1</td>
              <td>Quang Thìn</td>
              <td>quangthin2000</td>
              <td>student@gmal.com</td>
              <td>0169522022</td>
              <td>
                <Button color="success" size="sm" className="mr-3">
                  Edit
                </Button>
                <Button color="danger" size="sm">
                  Delete
                </Button>
              </td>
            </tr>
          </tbody>
        </Table>
      </div>
    );
  }
}
